import React, { useState } from 'react';
import PeopleOutlineTwoToneIcon from '@material-ui/icons/PeopleOutlineTwoTone';
import {
  Paper,
  makeStyles,
  TableBody,
  TableRow,
  TableCell,
  Toolbar,
  InputAdornment,
} from '@material-ui/core';
import useTable from '../../components/useTable';
import * as Service from '../../services/Service';
import Income from './Income';
import Expense from './Expense';
import Transfer from './Transfer';

import Controls from '../../components/controls/Controls';
import { Search } from '@material-ui/icons';
import AddIcon from '@material-ui/icons/Add';
import Popup from '../../components/Popup';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import CloseIcon from '@material-ui/icons/Close';

const headCells = [
  { id: 'date', label: 'Дата' },
  { id: 'type', label: 'Тип' },
  { id: 'money', label: 'Сумма' },
  { id: 'balanceId', label: 'Счет' },
  { id: 'categoryId', label: 'Категория' },
  { id: 'contractorId', label: 'Контрагент' },
  { id: 'projectId', label: 'Проект' },
];

export default function Overview() {
  const [recordForEdit, setRecordForEdit] = useState(null);
  const [records, setRecords] = useState(Service.getAllTransactions());
  const [filterFn, setFilterFn] = useState({
    fn: (items) => {
      return items;
    },
  });
  const [openIncome, setOpenIncome] = useState(false);

  const { TblContainer, TblHead, TblPagination, recordsAfterPagingAndSorting } = useTable(
    records,
    headCells,
    filterFn,
  );

  const handleSearch = (e) => {
    let target = e.target;
    setFilterFn({
      fn: (items) => {
        if (target.value == '') return items;
        else return items.filter((x) => x.fullName.toLowerCase().includes(target.value));
      },
    });
  };

  const addOrEdit = (transaction, resetForm) => {
    if (transaction.id == 0) Service.insertTransaction(transaction);
    else Service.updateTransaction(transaction);
    resetForm();
    setRecordForEdit(null);
    setOpenIncome(false);
    setRecords(Service.getAllTransactions());
  };

  return (
    <>
      <Toolbar>
        <Controls.Button
          text="Доход"
          variant="outlined"
          startIcon={<AddIcon />}
          onClick={() => {
            setOpenIncome(true);
            setRecordForEdit(null);
          }}
        />
      </Toolbar>
      <TblContainer>
        <TblHead />
        <TableBody>
          {recordsAfterPagingAndSorting().map((item) => (
            <TableRow key={item.id}>
              <TableCell>{item.date.substring(0, 10)}</TableCell>
              <TableCell>{item.type}</TableCell>
              <TableCell>{item.money}</TableCell>
              <TableCell>{item.balance}</TableCell>
              <TableCell>{item.categoryId}</TableCell>
              <TableCell>{item.contractorId}</TableCell>
              <TableCell>{item.projectId}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </TblContainer>
      <TblPagination />
      <Popup title="Доход" openPopup={openIncome} setOpenPopup={setOpenIncome}>
        <Income recordForEdit={recordForEdit} addOrEdit={addOrEdit} />
      </Popup>
      {/* <Popup title="Доход" openPopup={openPopup} setOpenPopup={setOpenPopup}>
        <Expense recordForEdit={recordForEdit} addOrEdit={addOrEdit} />
      </Popup>
      <Popup title="Доход" openPopup={openPopup} setOpenPopup={setOpenPopup}>
        <Income recordForEdit={recordForEdit} addOrEdit={addOrEdit} />
      </Popup> */}
    </>
  );
}
